exports.connect = function(req, res, options, query) {
    options.path += '?';
    for(q in query) {
        options.path += q + '=' + query[q] + '&';
    }
    options.path = options.path.substring(0, options.path.length - 1);
    console.log("PATH: "+options.path);
    require("http").get(options, function(get_res) {
        get_res.on('data', function(chunk) {
            res.write(chunk);
        });
        get_res.on('end', function() {
            res.end();
        });
    }).on('error', function(err) {
        res.end(JSON.stringify({
            error: err,
            on: 'HTTP.get'
        }));
    });
}